class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size(), k = 0;
        vector<int> finish(n);
        int pending = n;
        vector<unordered_set<int>> st(n);
        for(auto& _st: st)
            _st.insert(0);
        for(int i = 0; i < n; i++) {
            if (nums[i] == 0) {
                finish[i] = 1;
                --pending;
            }
        }
        if (pending == 0)
            return 0;
        for(const auto & q: queries) {
            ++k;
            int l = q[0], r = q[1], v = q[2];
            for(int i = l; i <= r; i++) {
                if (finish[i] == 1 || v > nums[i])
                    continue;
                if (v == nums[i]) {
                    finish[i] = 1;
                    if (--pending == 0)
                        return k;
                    continue;
                }
                unordered_set<int> tmp;
                for(const auto& existing: st[i]) {
                    tmp.insert(existing+v);
                }
                if (tmp.find(nums[i]) != tmp.end()) {
                    finish[i] = 1;
                    if (--pending == 0)
                        return k;
                } else {
                    for(const auto& _v: tmp)
                        st[i].insert(_v);
                }
            }
        }
        return -1;

    }
};

