class Solution {

    public boolean canBeZeroed(int target, List<Integer> w){
        // System.out.println(target+" "+w);
        if(target==0)return true;
        if(w.size()==0){
            return target==0;
        }        
        boolean dp[][] = new boolean[w.size()][target+1];
        // System.out.println(target+" "+w);
        if(w.get(0)<=target){
            dp[0][w.get(0)] = true;
        }
        dp[0][0] = true;
        for(int i=1;i<w.size();i++){
            int e = w.get(i);             
            for(int j=0;j<=target;j++){
                dp[i][j] = dp[i-1][j];
            }
            if(e<=target){
                dp[i][e] = true;
            }
            for(int j=0;j<=target;j++){
                if(e+j<=target){
                    dp[i][e+j] = dp[i][e+j]||dp[i-1][j];
                }
            }
        }
        return dp[w.size()-1][target];
    }

    public int minZeroArray(int[] nums, int[][] queries) {
        int n = nums.length;
        int zeroes=0;
        for(int x : nums){
            if(x==0)zeroes++;
        }
        if(zeroes==n){
            return 0;
        }
        ArrayList<Integer> q[] = new ArrayList[n];
        for(int i=0;i<n;i++)q[i] = new ArrayList();
        int nq = 0;
        boolean canz[] = new boolean[n];
        int sums[] = new int[n];
        for(int query[] : queries){
            nq++;
            for(int i=query[0];i<=query[1];i++){
                q[i].add(query[2]);
                sums[i]+=query[2];
            }
            int can = 0;
            for(int i=0;i<n;i++){
                if(canz[i]){
                    can++;
                    continue;
                }
                boolean result = (sums[i]>=nums[i]) && (canz[i]||canBeZeroed(nums[i],q[i]));
                canz[i] = canz[i]||result;
                if(canz[i]){
                    can++;
                }
            }
            if(can==n)return nq;
        }
        return -1;
    }
}

