class Solution {
int solve(int num , vector<vector<int>>&queries,int index,int i,vector<vector<int>>&dp){
    if(index==queries.size() && num>0){
        return 1e9;
    }
    if(num<0){
        return 1e9;
            }
    if(num==0){
        return index;
    }
    if(dp[num][index]!=-1){
        return dp[num][index];
    }
    int notpick = solve(num,queries,index+1,i,dp);
    int pick = 1e9;
    int low = queries[index][0];
    int high = queries[index][1];
    int val = queries[index][2];
    if(i>=low && i<=high){
        pick = solve(num-val,queries,index+1,i,dp);
    }
    return dp[num][index]=min(pick,notpick);
}
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n=nums.size();
        int m = queries.size();
        vector<int>temp(n);
        for(int i=0;i<n;i++){
            vector<vector<int>>dp(nums[i]+1,vector<int>(m,-1));
            temp[i] = solve(nums[i],queries,0,i,dp);
        }
        int maxi = -1e9;
        for(auto i : temp){
            maxi = max(i,maxi);
        }
        if(maxi ==-1e9 || maxi ==1e9){
            return -1;
        }
        return maxi;
    }
};

