class Solution {
public:
    bool check(vector<int> &a, vector<vector<int>> &q, int k) {
        int n = a.size();
        vector<map<int, int>> dec(n);

        for (int i = 0; i < k; i++) {
            int l = q[i][0], r = q[i][1], v = q[i][2];
            for (int j = l; j <= r; j++) {
                dec[j][v]++;
            }
        }

        for (int i = 0; i < n; i++) {
            int required = a[i];
            if (required == 0) continue;

            bitset<1001> dp;
            dp[0] = 1;

            for (auto& entry : dec[i]) {
                int value = entry.first, count = entry.second;
                bitset<1001> newDp = dp;
                for (int j = 0; j < count; j++) {
                    newDp |= (newDp << value);
                }
                dp = newDp;
            }

            if (!dp[required]) return false;
        }
        return true;
    }
    int minZeroArray(vector<int>& a, vector<vector<int>>& q) {
        int left = 0, right = q.size(), result = -1;

        while (left <= right) {
            int mid = left + (right - left) / 2;
            if (check(a, q, mid)) {
                result = mid;
                right = mid - 1;
            } else {
                left = mid + 1;
            }
        }
        return result;
    }
};

