class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        return sln1(nums, queries);
    }

    private int sln1(int[] nums ,int[][] qs){
        int n = nums.length;
        int m = qs.length;

        int l = -1, r = m-1;
        int res = -1;
        while(l<=r){
            int mid = l+r>>1;
            int[][] arr = new int[n+1][11];

            for(int i = 0;i<=mid;i++){
                int left = qs[i][0];
                int right = qs[i][1];
                int v = qs[i][2];
                arr[left][v]++;
                arr[right+1][v]--;
            }
            int[] prev = new int[11];
            boolean valid = true;
            for(int i =0 ;i<n;i++){
                for(int j = 0;j<prev.length;j++) arr[i][j] += prev[j];
                if(!helper(arr[i] ,nums[i])){
                    valid = false;
                    break;
                }
                prev = arr[i];
            }
            if(valid){
                res = mid+1;
                r = mid-1;
            }else{
                l = mid+1;
            }
        }
        return res;
    }

    private boolean helper(int[] arr, int t ){
        boolean[] dp = new boolean[t+1];
        dp[0] = true;
        for(int k = 0;k<arr.length;k++){
            int v = arr[k];
            for(int j = 0;j<v;j++){
                for(int i = t;i>=k;i--){
                    dp[i] = dp[i] || dp[i-k];
                }
            }
        }
        return dp[t];
    }
}

