/**
 * @param {number[]} nums
 * @param {number[][]} queries
 * @return {number}
 */
var minZeroArray = function(nums, queries) {
    let dp;

    let best = 0;
    for(let i = 0; i < nums.length; ++i) {
        dp = [];
        let steps = Math.max(best, dfs(i, 0));
        if(steps >= Infinity) { return -1; }

        best = Math.max(best, steps);
    }

    return best;

    function dfs(digit, qIdx) {
        if(nums[digit] === 0) {
            return qIdx;
        }

        if(qIdx >= queries.length) { return Infinity; }

        let dpIdx = nums[digit]*queries.length + qIdx;
        if(dp[dpIdx]) { return dp[dpIdx]; }

        let best = Infinity;

        let attempted = false;

        for(let i = qIdx; i < queries.length; ++i) {
            let [l, r, val] = queries[i];
            if(!(l <= digit && r >= digit && val <= nums[digit])) { continue; }

            attempted = true;
            qIdx = i;

            nums[digit] -= val;
            best = Math.min(best, dfs(digit, i+1));
            nums[digit] += val;
            break;
        }

        if(attempted && best > (qIdx+1)) {
            best = Math.min(best, dfs(digit, qIdx+1));
        }

        dp[dpIdx] = best;

        return best;

    }
};

