            if rem < 0 or curr == len(arr):
                return float('inf')
            if (curr, rem) not in dp:
                dp[(curr, rem)] = min(get_min(curr+1, rem-arr[curr][1]), get_min(curr+1, rem))
            return dp[(curr, rem)]

        min_k = 0
        for i in range(len(nums)):
            if nums[i] != 0:
                for k, (l, r, v) in enumerate(queries):
                    if l <= i and i <= r:
                       arr.append((k, v))
                idx = get_min(0, nums[i])
                dp.clear()
                if idx == float('inf'):
                    return -1
                min_k = max(min_k, arr[idx][0]+1)
                arr.clear()

        return min_k

