#include <bits/stdc++.h>
using namespace std;

class Solution {
public:
    // Function to check if we can make all nums[i] zero using first 'k' queries
    bool canMakeZero(vector<int>& nums, vector<vector<int>>& queries, int k) {
        int n = nums.size();
        vector<vector<int>> applied_values(n);  // Store applied values for each index

        // Apply first k queries
        for (int i = 0; i < k; i++) {
            int l = queries[i][0], r = queries[i][1], v = queries[i][2];
            for (int j = l; j <= r; j++) {
                applied_values[j].push_back(v);
            }
        }

        // Check if each nums[i] can be reduced to 0 using a subset of applied values
        for (int i = 0; i < n; i++) {
            if (nums[i] == 0) continue;
            if (applied_values[i].empty()) return false;  // No values available to reduce nums[i]

            // Check if a subset of applied_values[i] can sum to nums[i]
            int target = nums[i];
            int sz = applied_values[i].size();
            vector<bool> dp(target + 1, false);
            dp[0] = true;

            for (int val : applied_values[i]) {
                for (int sum = target; sum >= val; sum--) {
                    dp[sum] = dp[sum] || dp[sum - val];
                }
            }
            if (!dp[target]) return false;
        }
        return true;
    }

    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int left = 0, right = queries.size(), ans = -1;

        while (left <= right) {
            int mid = (left + right) / 2;
            if (canMakeZero(nums, queries, mid)) {
                ans = mid;
                right = mid - 1;  // Try to minimize k
            } else {
                left = mid + 1;
            }
        }
        return ans;
    }
};

