class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int max = -1;
        int n = queries.length;
        for (int i = 0; i < nums.length; i++) {
            int num = nums[i];
            boolean[][] dp = new boolean[n + 1][num + 1];
            for (int j = 0; j <= n; j++) {
                dp[j][0] = true;
            }
            for (int j = 1; j <= queries.length; j++) {
                int l = queries[j - 1][0];
                int r = queries[j - 1][1];
                int val = queries[j - 1][2];
                if (i < l || i > r) {
                    if (num >= 0) System.arraycopy(dp[j - 1], 1, dp[j], 1, num);
                    continue;
                }
                for (int k = 1; k <= num; k++) {
                    dp[j][k] = dp[j - 1][k];
                    if (k >= val) {
                        dp[j][k] = dp[j][k] || dp[j - 1][k - val];
                    }
                }
            }
            boolean find = false;
            for (int j = 0; j <= n; j++) {
                if (dp[j][num]) {
                    find = true;
                    max = Math.max(max, j);
                    break;
                }
            }
            if (!find) {
                return -1;
            }
        }
        return max;
    }
}

