class Solution {
    int dfs_find_min_idx(int target, int idx, vector<pair<int, int>> &q, vector<vector<int>> &dp) {
        if (target == 0) {
            return q[idx - 1].second;
        }
        if (idx == q.size()) {
            return INT_MAX;
        }
        if (dp[target][idx] != -1) {
            return dp[target][idx];
        }

        dp[target][idx] = dfs_find_min_idx(target, idx + 1, q, dp);
        if (target >= q[idx].first) {
            dp[target][idx] = min(dp[target][idx], dfs_find_min_idx(target - q[idx].first, idx + 1, q, dp));
        }
        return dp[target][idx];
    }

    void init_dp(vector<vector<int>> &dp) {
        for (int i = 0; i < dp.size(); ++i) {
             for (int j = 0; j < dp[i].size(); ++j) {
                 dp[i][j] = -1;
             }
        }
        return;
    }

public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int const NLEN = nums.size();
        int const QLEN = queries.size();

        vector qOnIdx(NLEN, vector<pair<int, int>>());
        for (int i = 0; i < QLEN; ++i) {
            for (int j = queries[i][0]; j <= queries[i][1]; ++j) {
                qOnIdx[j].push_back({queries[i][2], i});
            }
        }

        int maxVal = *max_element(nums.begin(), nums.end());
        vector dp(maxVal + 1, vector<int>(QLEN));
        int ans = -1;

        for (int i = 0; i < NLEN; ++i) {
            init_dp(dp);
            if (nums[i] > 0) {
                ans = max(ans, dfs_find_min_idx(nums[i], 0, qOnIdx[i], dp));
            }
        }

        return (ans == INT_MAX) ? -1 : ans + 1;
    }
};

