class Solution {
public:
    int func(vector<vector<int>>& queries, int i, int val, int idx, vector<vector<int>>& dp) {
        if(val == 0) return 0;
        if(i == queries.size() || val < 0) return INT_MAX;
        if(dp[i][val] != -1) return dp[i][val];

        int take = INT_MAX;
        if(idx >= queries[i][0] && idx <= queries[i][1]) {
            int res = func(queries, i + 1, val - queries[i][2], idx, dp);
            if(res != INT_MAX) take = 1 + res;
        }

        int nottake = INT_MAX;
        int res = func(queries, i + 1, val, idx, dp);
        if(res != INT_MAX) nottake = 1 + res;

        return dp[i][val] = min(take, nottake);
    }

    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int m = queries.size();
        int ans = 0;

        for(int i = 0; i < n; i++) {
            vector<vector<int>> dp(1001, vector<int>(1001, -1));
            int res = func(queries, 0, nums[i], i, dp);
            if(res == INT_MAX) return -1;
            ans = max(ans, res);
        }

        return ans;
    }
};

