class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int m = queries.size();
        vector<vector<pair<int, int>>> v(n); // Stores relevant queries for each index
        vector<int> required_ks(n, -1); // Stores the required k for each index

        // Step 1: For each index in nums, collect all queries that affect it
        for (int i = 0; i < n; ++i) {
            if (nums[i] == 0) {
                required_ks[i] = 0; // No queries needed for zero elements
                continue;
            }
            for (int pos = 0; pos < m; ++pos) {
                int l = queries[pos][0];
                int r = queries[pos][1];
                int val = queries[pos][2];
                if (l <= i && i <= r) {
                    v[i].push_back({pos, val}); // Store query position and value
                }
            }
        }

        // Step 2: For each index, compute the minimum k using dynamic programming
        for (int i = 0; i < n; ++i) {
            if (nums[i] == 0) continue; // Skip zero elements

            unordered_map<int, int> dp; // Tracks the earliest query index for each sum
            dp[0] = -1; // Base case: sum 0 can be achieved before any queries
            int target = nums[i];

            for (const auto& query : v[i]) {
                int pos = query.first;
                int val = query.second;

                // Create a copy of current dp to avoid modifying it while iterating
                auto current_dp = dp;
                for (const auto& entry : current_dp) {
                    int s = entry.first;
                    int p = entry.second;
                    int new_s = s + val;

                    if (new_s > target) continue; // Skip sums exceeding the target

                    // Update dp with the earliest query index for the new sum
                    if (dp.find(new_s) == dp.end() || dp[new_s] > pos) {
                        dp[new_s] = pos;
                    }
                }
            }

            // Check if the target sum is achievable
            if (dp.find(target) == dp.end()) {
                return -1; // Target sum cannot be achieved
            }
            required_ks[i] = dp[target] + 1; // Store the required k for this index
        }

        // Step 3: Determine the overall minimum k
        int max_k = 0;
        for (int k : required_ks) {
            if (k == -1) return -1; // If any index cannot be zeroed, return -1
            max_k = max(max_k, k);
        }

        return max_k > m ? -1 : max_k; // Ensure k does not exceed the number of queries
    }
};

