class Solution {
public:
    int minZeroArray(vector<int>& a, vector<vector<int>>& q) {
        int n = a.size();
        using vi = vector<int>;
        using vvi = vector<vector<int>>;

        auto ok = [](int s, vi &b) -> bool {
            if (s == 0) return true;
            vi dp(s + 1, 0);
            dp[0] = 1;
            for (int i : b) {
                for (int j = s; j >= i; j--) {
                    dp[j] = dp[j] | dp[j - i];
                }
            }
            return dp[s] == 1;
        };

        auto check = [&](int k) -> bool {
            vvi b(n);
            for (int i = 0; i < k; i++) {
                for (int j = q[i][0]; j <= q[i][1]; j++) {
                    b[j].push_back(q[i][2]);
                }
            }
            for (int i = 0; i < n; i++) {
                if (!ok(a[i], b[i])) return false;
            }
            return true;
        };

        int l = 0, r = q.size(), ans = 1e9;
        while (l <= r) {
            int m = (l + r) / 2;
            if (check(m)) {
                ans = min(ans, m);
                r = m - 1;
            } else {
                l = m + 1;
            }
        }
        return (ans == 1e9) ? -1 : ans;
    }
};

