int minZeroArray(vector<int>&nums,vector<vector<int>>&queries){
    int n = nums.size();
    vector<bitset<1001>>dp(n);
    for(int j = 0;j<n;j++){
        dp[j].set(0);
    }
    if(all_of(nums.begin(),nums.end(),[](int x){ return x == 0;})){
        return 0;
    }
    for(int k = 0;k<queries.size();k++){
        int l = queries[k][0],r=queries[k][1],v=queries[k][2];
        for(int j = l;j<=r;j++){
            bitset<1001>shifted;
            for(int i = 0;i<=1000-v;i++){
                if(dp[j][i]){
                    shifted.set(i+v);
                }
            }
            dp[j]|=shifted;
            for(int i = nums[j]+1;i<=1000;i++){
            dp[j].reset(i);
        }
        }
        if(all_of(dp.begin(),dp.end(),[&](bitset<1001>&b){ return b[nums[&b-dp.data()]];})){
            return k+1;
        }
    }
    return -1;
}
};

