    bool isOk(int tar, vector<int>& freq){
        // cout << tar <<" -> ";
        // for(auto& x: freq) cout << x <<" ";
        // cout <<"n";
        if(tar == 0) return true;

        int sum = 0;
        for(int i=1; i<=10; i++) {
            if( tar > sum && (((tar-sum) % i) == 0) && ((tar-sum)/i <= freq[i]) ) return true;
            sum += (i* freq[i]);
            if(sum == tar) return true;
        }
        if(sum < tar) return false;
        if(dp.find({tar, freq}) != dp.end()) return dp[{tar, freq}];
        bool flag = false;
        for(int i=1; i<=10; i++){
            if(i > tar) break;
            if(freq[i] == 0) continue;

            tar -= i;
            freq[i]--;
            flag = isOk(tar, freq);
            tar += i;
            freq[i]++;
            if(flag) break;
        }
        dp[{tar, freq}] = flag;
        return flag;
    }
