                if(isOk(nums[i], active) == false) break;
                while(remove.size() > 0){
                    auto it = remove.begin();
                    if(it->first == i) {  active[it->second]--; remove.erase(it); }
                    else break;
                }
                i++;
                while(avl.size() > 0 && (avl.begin()->first) <= i){
                    vector<int> nxt = avl.begin()->second;
                    avl.erase(avl.begin());
                    active[nxt[2]]++;
                    remove.insert({nxt[1], nxt[2]});
                }
            }
            if(i == n) return k;
            k++;
            if(x[1] < i) continue;
            if(x[0] > i){ avl.insert({x[0], x}); continue; }
            active[x[2]]++;
            remove.insert({x[1], x[2]});
        }
        while(i < n){
            if(isOk(nums[i], active) == false) break;
            while(remove.size() > 0){
                auto it = remove.begin();
                if(it->first == i) {  active[it->second]--; remove.erase(it); }
