from collections import defaultdict
from typing import List

class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:

        def subset_sum(values, indices, target):

            dp = {0: -1}  

            for val, query_idx in zip(values, indices):
                new_dp = dict(dp)  
                for s in dp:
                    new_sum = s + val
                    if new_sum == target:
                        return max(dp[s], query_idx) 
                    if new_sum <= target and new_sum not in new_dp:
                        new_dp[new_sum] = max(dp[s], query_idx)
                dp = new_dp 

            return -1  

        n = len(nums)
        q = len(queries)
        graph = defaultdict(list)  
        last_query_index = -1  


        for i, (l, r, val) in enumerate(queries):
            for index in range(l, r + 1):
                graph[index].append((val, i))  


        for index in range(n):
            if nums[index] == 0:
                continue  

            values = [v[0] for v in graph[index]] 
            indices = [v[1] for v in graph[index]]  

            last_used = subset_sum(values, indices, nums[index])  

            if last_used == -1:
                return -1  

            last_query_index = max(last_query_index, last_used)  

        return last_query_index + 1

