class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n, m = len(nums), len(queries)
        ans = 0

        def solve(num, qi, ni):
            if dp.get((num, qi), None) == None:
                if qi == 0:
                    dp[(num, qi)] = num == 0 or (queries[qi][0] <= ni <= queries[qi][1] and num == queries[qi][2])
                else:
                    dp[(num, qi)] = (queries[qi][0] <= ni <= queries[qi][1] and num >= queries[qi][2] and solve(num - queries[qi][2], qi-1, ni)) or solve(num, qi-1, ni)
            return dp[(num, qi)]

        for i in range(n):
            if nums[i] == 0: continue
            j = 0
            dp = {}
            while j < m:
                if solve(nums[i], j, i):
                    ans = max(ans, j+1)
                    break
                j += 1
            if j == m:
                ans = -1
                break
        return ans

