class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int low = 0, high = queries.size();
        int res = -1;

        while(low <= high)
        {
            int mid = low + (high - low) / 2;

            if(isValid(nums, queries, mid)) {
                res = mid;
                high = mid - 1;
            }
            else {
                low = mid + 1;
            }
        }

        return res;
    }

    bool isValid(vector<int>& nums, vector<vector<int>>& queries, int k) 
    {
        for(int idx=0;idx<nums.size();idx++)
        {
            int target = nums[idx];
            vector<vector<int>> dp(k + 1, vector<int>(target + 1, -1));
            if(!solve(idx, 0, k, 0, target, queries, dp)) {
                return false;
            }
        }
        return true;
    }

    bool solve(int numIdx, int idx, int k, int sum, int target, vector<vector<int>>& queries, vector<vector<int>>& dp) 
    {
        if(sum > target) {
            return 0;
        }

        if(sum == target) {
            return 1;
        }

        if(idx == k) {
            return 0;
        }

        if(dp[idx][sum] != -1) {
            return dp[idx][sum];
        }

        int left = queries[idx][0];
        int right = queries[idx][1];
        int value = queries[idx][2];

        int res = 0;

        // the idx is in range of query
        if(numIdx >= left and numIdx <= right)
        {
            // either exclude or include
            int exc = solve(numIdx, idx + 1, k, sum, target, queries, dp);
            int inc = solve(numIdx, idx + 1, k, sum + value, target, queries, dp);
            res = exc || inc;
        }
        else {
            // if not in range, can only skip
            res = solve(numIdx, idx + 1, k, sum, target, queries, dp);
        }

        return dp[idx][sum] = res;
    }
};

