                int ri = q[j][1];
                int value = q[j][2];
                if (le <= i && i <= ri)   qlv.emplace_back(j, value);

            }

            for (auto fg : qlv) {
                sl += fg.second;
            }
            if (sl < gh) {
                return -1;
            }
            for (auto pd : qlv) {
                int j = pd.first;
                int value = pd.second;
                unordered_map<int, int> n_dp(dpp);
                for (auto e : dpp) {
                    int cm = e.first;
                    int cj = e.second;
                    int nm = cm + value;
                    if (nm > gh) continue;
                    int nj = max(cj, j);
                    if (n_dp.find(nm) == n_dp.end() || nj < n_dp[nm]) {
                        n_dp[nm] = nj;
                    }
                }
                dpp.swap(n_dp);
            }

            if (dpp.find(gh) == dpp.end()) {
                return -1;
            }
            r_k[i] = dpp[gh] + 1;
        }

        int max_k = 0;
        for (auto k : r_k) {
            if (k == -1) return -1;
            if (k > max_k) {
                max_k = k;
            }
        }
        return max_k;
    }
};

