        if (z) return 0;

        for (int i = 0; i < n; i++) dp[i].insert(0);

        for (int i = 0; i < queries.size(); i++) {
            int l = queries[i][0], r = queries[i][1], v = queries[i][2];
            bool ok = true;

            for (int j = l; j <= r; j++) {
                unordered_set<int> newSet;
                for (int x : dp[j]) {
                    if (x + v < 1001) newSet.insert(x + v);
                }
                for (int x : newSet) dp[j].insert(x);
            }

            for (int j = 0; j < n; j++) {
                if (dp[j].find(nums[j]) == dp[j].end()) {
                    ok = false;
                    break;
                }
            }
            if (ok) return i + 1;
        }
        return -1;
    }
};

