class Solution {

    int minSum(vector<pair<int,int>> &vals, int num, int index, vector<vector<int>> &DP)
    {
        int sum = 0;
        if(num < 0 || index >= vals.size()) return INT_MAX;
        if(DP[num][index] != -1) return DP[num][index];

        for(auto c : vals) sum += c.first;
        if(sum < num) return INT_MAX;
        if(num - vals[index].first == 0) return  vals[index].second + 1;

        int take = INT_MAX, noTake = INT_MAX;
        take = minSum(vals, num - vals[index].first, index+1, DP);
        noTake = minSum(vals, num, index+1, DP);

        DP[num][index] =  min(take, noTake);
        return min(take, noTake);
    }
    int check(vector<vector<int>>& queries, int num, int index)
    {
        vector<pair<int,int>> vals;
        for(int i=0; i<queries.size(); i++)
        {
            if(queries[i][0] <= index && queries[i][1] >= index)
            {
                vals.push_back({queries[i][2], i});
            }
        }
        vector<vector<int>> DP(num + 1, vector<int>(vals.size(), -1));
        int ans = minSum(vals, num, 0, DP);
        return ans;
    }
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int ans = 0;
        for(int i=0; i<nums.size(); i++)
        {
            if(nums[i] == 0) continue;
            int k = check(queries, nums[i], i);
            if(k == INT_MAX) return -1;
            ans = max(ans, k);
        }
        return ans == INT_MAX ? -1 : ans;
    }
};

