class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n, q = len(nums), len(queries)

        def f(k):
            for i in range(n):
                c = sorted([queries[j][2] for j in range(k) if queries[j][0] <= i <= queries[j][1]])

                if sum(c) < nums[i]: return False

                dp = 1
                for co in c:
                    dp |= dp << co

                    if (dp >> nums[i]) & 1:
                        break

                if ((dp >> nums[i]) & 1) == 0:
                    return False
            return True

        lo, hi = 0, q
        ans = -1
        while lo <= hi:
            m = (lo + hi) // 2

            if f(m):
                hi = m - 1
                ans = m
            else:
                lo = m + 1
        return ans

