import java.util.*;

class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int n = nums.length;
        Pair<int[], int[][]> dataPair = new Pair<>(nums, queries);

        BitSet[] dp = new BitSet[n];
        for (int i = 0; i < n; i++) {
            dp[i] = new BitSet(1001);
            dp[i].set(0, true);
        }

        boolean isAllZero = Arrays.stream(nums).allMatch(num -> num == 0);
        if (isAllZero) return 0;

        for (int queryIndex = 0; queryIndex < queries.length; queryIndex++) {
            int left = queries[queryIndex][0];
            int right = queries[queryIndex][1];
            int value = queries[queryIndex][2];

            for (int i = left; i <= right; i++) {
                dp[i].or(shiftLeft(dp[i], value));

                for (int pos = nums[i] + 1; pos < 1001; pos++) {
                    dp[i].clear(pos);
                }
            }

            boolean allTargetsMet = true;
            for (int i = 0; i < n; i++) {
                if (!dp[i].get(nums[i])) {
                    allTargetsMet = false;
                    break;
                }
            }

            if (allTargetsMet) return queryIndex + 1;
        }

        return -1;
    }

    private BitSet shiftLeft(BitSet bitset, int positions) {
        BitSet shifted = new BitSet(bitset.length() + positions);
        for (int i = bitset.nextSetBit(0); i >= 0; i = bitset.nextSetBit(i + 1)) {
            shifted.set(i + positions);
        }
        return shifted;
    }

    private static class Pair<A, B> {
        A first;
        B second;

        Pair(A first, B second) {
            this.first = first;
            this.second = second;
        }
    }
}

