class Solution {
    bool solve(int i,int s,vector<int>&arr,vector<vector<int>>&dp){
        int n=arr.size();
        if(s==0) return 1;
        if(i>=n) return 0;
        if(dp[i][s]!=-1) return dp[i][s];
        bool a,b;
        a=b=0;
        b=solve(i+1,s,arr,dp);
        if(s-arr[i]>=0){
            a=solve(i+1,s-arr[i],arr,dp);
        }
        return dp[i][s]=a || b; 

    }
    bool check(vector<int>&nums,vector<vector<int>>&q,int k){

        int n=nums.size();
        vector<vector<int>>arr(n);
        for(int i=0;i<=k;i++){
            int l=q[i][0];
            int r=q[i][1];
            int v=q[i][2];
            for(int j=l;j<=r;j++){
                arr[j].push_back(v);
            }

        }

        for(int i=0;i<n;i++){
            vector<vector<int>>dp(arr[i].size(),vector<int>(nums[i]+1,-1));
            bool f=solve(0,nums[i],arr[i],dp);
            if(!f) return 0;
        }
        return 1;
    }
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& q) {
        int l=0;
        int r=q.size()-1;
        int ans=r;
        bool f=0;
        for(auto i:nums){
            if(i!=0) f=1;
        }
        if(f==0) return 0;
        f=0;
        while(l<=r){
            int m=l+(r-l)/2;
            if(check(nums,q,m)){
                f=1;
                ans=m;
                r=m-1;
            }
            else l=m+1;
        }
        if(f==0) return -1;
        return ans+1;
    }
};

