    int get_max(vector<pair<int, int>> a, int t) {
        if (t == 0) return 0;
        int n = a.size();

        vector<vector<int>> dp(n, vector<int>(t + 1, -1));

        auto fun = [&](int i, int ts, auto fun) -> int {
            if (i == n) {
                return 1e9;
            }

            if (dp[i][ts] != -1) {
                return dp[i][ts];
            }

            int best = 1e9;
            if (a[i].ff <= ts) {
                if (a[i].ff == ts) {
                    return dp[i][ts] = a[i].ss;
                }

                best = fun(i + 1, ts - a[i].ff, fun);
            }

            int skip = fun(i + 1, ts, fun);

            return dp[i][ts] = min(best, skip);
        };

        int x = fun(0, t, fun);

        // cout << t << " => n";
        // for (auto it : a) {
        //     cout << it.ff << " " << it.ss << "n";
        // }
        // cout << x << "nn";
        if (x == 1e9) return -1;
        return x;
    } 

public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size(), q = queries.size();

        map<int, vector<pair<int, int>>> mp;        
        for (int k = 0; k < q; ++k) {
            int s = queries[k][0], e = queries[k][1];

            for (int i = s; i <= e; ++i) {
                mp[i].push_back({queries[k][2], k + 1});
            }
        }

        int ans = -1;

        for (int i = 0; i < n; ++i) {
            int cur = get_max(mp[i], nums[i]);
            if (cur == -1) return -1;

            ans = max(ans, cur);
        }

        return ans;
    }
