            isZeros = [False] * n
            remainNonzero = 0
            for i in range(n):
                if nums[i] == 0:
                    isZeros[i] = True
                else:
                    remainNonzero += 1

            if remainNonzero == 0:
                return 0


            possibleSet = [set() for _ in range(n)]
            for i in range(n):
                possibleSet[i].add(nums[i])

            for i in range(len(queries)):
                l, r, val = queries[i]

                for j in range(l, r + 1):
                    if isZeros[j] == True:
                        continue

                    newSet = set(possibleSet[j])
                    for x in possibleSet[j]:
                        if x - val >= 0:
                            newSet.add(x - val)
                    possibleSet[j] = newSet

                    if 0 in possibleSet[j]:
                        isZeros[j] = True
                        remainNonzero -= 1


                if remainNonzero == 0:
                    return i + 1
            return -1
        return sol1()

