class Solution {
public:
    int ans = -1;
    bool solve(int i, int t, int m, int &maxi, vector<pair<int, int>> &arr, 
vector<vector<int>> &dp){
        if(t == 0){
            maxi = min(maxi, m);
            return true;
        }
        if(i == arr.size() || t < 0) return false;
        if(dp[i][t] != -1) return dp[i][t];

        int take = solve(i+1, t - arr[i].first, arr[i].second, maxi, arr, dp);
        int nt = solve(i+1, t, m, maxi, arr, dp);

        return dp[i][t] = (take | nt);
    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {

       int n = nums.size(), q = queries.size();
        vector<vector<pair<int, int>>> arr(n);

        for(int i = 0; i < q; i++){
            int l = queries[i][0];
            int r = queries[i][1];
            int val = queries[i][2];

            for(int j = l; j <= r; j++){
                arr[j].push_back({val, i});
            }
        }

        for(int i = 0; i < n; i++){
            int target = nums[i];
            vector<pair<int, int>> v = arr[i];
            vector<vector<int>> dp(v.size(), vector<int> (target+1, -1));
            int maxi = 1e9;
            if(solve(0, target, -1, maxi, v, dp)){
                ans = max(ans, maxi);
            }
            else return -1;
        }
        return ans + 1;

    }
};

