		ans = kroCheck(pos + 1, K, cursum, queries, loc);
		if (ans) return true;

		int l = queries[pos][0], r = queries[pos][1], val = queries[pos][2];
		if (loc >= l and loc <= r) {
			ans |= kroCheck(pos + 1, K, cursum - val, queries, loc);
		}
		return ans;
	}

	int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
		n = sz(nums);
		int z = sz(queries);

		int mx = 0;
		for (auto &i : nums) mx = max(mx, i);
		if (mx == 0) return 0;

		auto possible = [&](int K) -> bool{

			bool ok = true;
			fo(i, 0, n - 1) {
				// check if it possible to make it zero using first K queries
				fo(pos, 0, K - 1) {
					fo(cursum, 0, nums[i]) dp[pos][cursum] = -1;
				}
				if (!kroCheck(0, K, nums[i], queries, i)) {
					ok = false;
					break;
				}
			}
			return ok;
		};

		int low = 1, high = z, mid, ans = -1;
		while (low <= high) {
			mid = (low + high) / 2;
			if (possible(mid)) {
				ans = mid;
				high = mid - 1;
			}
			else {
				low = mid + 1;
			}
		}
		return ans;
	}
};

