class Solution {
public:
    bool isPos(vector<int>& nums, vector<vector<int>>& queries,int k) {
        int n = nums.size();
        for (int j = 0; j < n; j++) {
            int tar = nums[j];
            if (tar == 0)
                continue;

            vector<bool> dp(tar + 1, false);
            dp[0] = true;
            for (int i = 0; i < k; i++) {
                if (queries[i][0] <= j && j <= queries[i][1]) {
                    int val = queries[i][2];
                    for (int sum = tar; sum >= val; sum--) {
                        if (dp[sum - val])
                            dp[sum] = true;
                    }
                }
            }
            if (!dp[tar])
                return false;
        }
        return true;
    }

    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int q = queries.size();
        int s = 0, e = q;
        int ans = -1;

        while (s <= e) {
            int mid = s + (e - s) / 2;

            if (isPos(nums, queries, mid)) {
                ans = mid;
                e = mid - 1;
            } else {
                s = mid + 1;
            }
        }
        return ans;
    }
};

