class Solution {
public:
    pair<bool, int> f(int target, vector<pair<int, int>>& v) {
        int n = v.size();
        vector<pair<bool, int>> dp(target + 1, {false, INT_MAX});
        dp[0] = {true, -1};
        for (auto& [num, i] : v) {
            for (int j = target; j >= num; j--) {
                if (dp[j - num].first) {
                    int max_index = max(dp[j - num].second, i);
                    if (!dp[j].first || max_index < dp[j].second) {
                        dp[j].first = true;
                        dp[j].second = max_index;
                    }
                }
            }
        }
        if (dp[target].first) {
            return {true, dp[target].second};
        }
        return {false, -1};
    }

    int minZeroArray(vector<int>& nums, vector<vector<int>>& q) {
        int n = nums.size();
        map<int, vector<pair<int, int>>> mp;
        for (int i = 0; i < q.size(); i++) {
            int li = q[i][0], ri = q[i][1], vali = q[i][2];
            for (int j = li; j <= ri; j++) {
                mp[j].push_back({vali, i});
            }
        }
        int result = 0;
        for (int j = 0; j < n; j++) {
            if (mp.find(j) == mp.end()) {
                if (nums[j] == 0) continue;
                else return -1;
            }
            auto res = f(nums[j], mp[j]);
            if (!res.first) return -1;
            int m_j = res.second;
            result = max(result, m_j + 1);
        }
        return result;
    }
};

