class Solution {
public:
    int minZeroArray(vector<int>& v, vector<vector<int>>& q) {
        int n = v.size();
        int m = q.size();
        int ans = 0;
        for (int i = 0; i < n; ++i) {
            int req = v[i];
            if (req == 0) {
                ans = max(ans, 0);
                continue;
            }

            vector<bool> dp(1001, false);
            dp[0] = true;
            int jal = -1;
            bool flag = false;

            for (int j = 0; j < m; ++j) {
                int l = q[j][0];
                int r = q[j][1];
                int d = q[j][2];
                if (i < l || i > r) {
                    continue;
                }

                vector<bool> new_dp = dp;
                for (int k = 1000; k >= 0; --k) {
                    if (dp[k] && k + d <= 1000) {
                        new_dp[k + d] = true;
                    }
                }

                if (new_dp != dp) {
                    dp = new_dp;
                    if (dp[req] && jal == -1) {
                        jal = j + 1;
                        flag = true;
                    }
                }
            }

            if (jal == -1 && !flag) {
                return -1;
            }

            ans = max(ans, jal);
        }

        return ans;
    }
};

