class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int q = queries.size();


        auto canAchieve = [&](int X) -> bool {

            vector<bitset<1001>> possible(n);
            for (int j = 0; j < n; j++) {
                possible[j].reset();
                possible[j].set(0, true); 
            }

            for (int i = 0; i < X; i++){
                int l = queries[i][0], r = queries[i][1], val = queries[i][2];
                for (int j = l; j <= r; j++){

                    possible[j] |= (possible[j] << val);
                }
            }

            for (int j = 0; j < n; j++){
                if (!possible[j].test(nums[j]))
                    return false;
            }
            return true;
        };


        int lo = 0, hi = q + 1;
        while (lo < hi) {
            int mid = lo + (hi - lo) / 2;
            if (canAchieve(mid)) hi = mid;
            else lo = mid + 1;
        }

        return lo > q ? -1 : lo;
    }


};

