class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size(), m = queries.size();
        int mx = ranges::max(nums);
        vector f(n, vector(mx + 1, false));
        for (int i = 0; i < n; i++) {
            f[i][0] = true;
        }

        int s = reduce(nums.begin(), nums.end(), 0);
        if (s == 0) {
            return 0;
        }

        for (int k = 0; k < m; k++) {
            auto& q = queries[k];
            int l = q[0], r = q[1], v = q[2];

            int ok = 0;
            for (int i = 0; i < n; i++) {
                if (i >= l and i <= r) {
                    for (int j = nums[i]; j >= v; j--) {
                        if (f[i][j - v]) {
                            f[i][j] = true;
                        }
                    }
                }
                ok += f[i][nums[i]];
            }
            if (ok == n) {
                return k + 1;
            }
        }
        return -1;
    }
};

