class Solution {
public:
    bool target(vector<int>& nums, int target){
        int n = nums.size();
        vector<bool> dp(target+1, false);
        dp[0] = 1;
        for(int it : nums){
            for(int j = target; j >= it; j--){
                dp[j] = dp[j] || dp[j-it];
            }
        }
        return dp[target];
    }
    bool check(vector<int>& nums, vector<vector<int>>& queries, int k){
        int n = nums.size();
        vector<int> temp = nums;
        vector<vector<int>> mp(n);
        for(int i = 0; i < k; i++){
            int l = queries[i][0], r = queries[i][1], val = queries[i][2];
            for(int j = l; j <= r; j++){
                mp[j].push_back(val);
            }
        }
        for(int i = 0; i < n; i++){
            vector<int>& nn = mp[i];
            if(!target(nn, nums[i]))
                return 0;
        }
        return 1;
    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int q = queries.size();
        bool f=1;
        for(int it : nums){
            if(it != 0){
                f = 0;
                break;
            }
        }
        if(f) return 0;
        int ans = -1;
        int l = 1, r = q;
        while(l <= r){
            int mid = (l + r) / 2;
            if(check(nums, queries, mid)){
                ans = mid;
                r = mid - 1;
            }
            else{
                l = mid + 1;
            }
        }
        return ans;
    }
};

