                vector<int> vals;
                for (int k = 1; k <= 10; k++) {
                    for (int j = 0; j < cnt[k]; j++)
                        vals.push_back(k);
                }

                // dbg(i, nums[i], vals);
                if (pos(nums[i], vals)) {
                    continue;
                } else {
                    return false;
                }
            }
            return true;
        };

        // dbg(get(2));
        int low = 0, high = q, ans = -1;
        while (low <= high) {
            int mid = (low + high) / 2;
            int ok = get(mid);
            // dbg(mid, ok);
            if (ok) {
                ans = mid;
                high = mid - 1;
            } else {
                low = mid + 1;
            }
        }
        return ans;
        // return -1;
    }
};

