class Solution {
public:
    int knapsack(int idx, int value, vector<vector<int>>& queries) {
        vector<int> dp(value + 1, 0);
        dp[0] = 1;
        if (value == 0) return 0; 
        for (int k = 0; k < queries.size(); ++k) {
            auto& query = queries[k];
            if (idx <= query[1] && idx >= query[0]) {
                for (int j = dp.size() - 1; j >= 0; --j) {
                    if (j - query[2] >= 0) {
                        dp[j] |= dp[j-query[2]];
                    }
                }
            }
            if (dp[value]) {
                return k+1;
            }
        }
        return INT_MAX;
    }

    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int k = 0;
        for (int i = 0; i < nums.size(); ++i) {
            int res = knapsack(i, nums[i], queries);
            if (res == INT_MAX) {
                return -1;
            }
            k = max(k, res);
        }

        return k;
    }
};

