using System;
using System.Collections.Generic;

public class Solution {
    public int MinZeroArray(int[] numbers, int[][] queries) {
        int size = numbers.Length;
        var dataPair = new Tuple<int[], int[][]>(numbers, queries);

        var bitMasks = InitializeBitMasks(size);
        if (CheckAllZero(numbers)) return 0;

        for (int queryIndex = 0; queryIndex < queries.Length; queryIndex++) {
            int left = queries[queryIndex][0];
            int right = queries[queryIndex][1];
            int shiftValue = queries[queryIndex][2];

            ApplyBitMask(bitMasks, numbers, left, right, shiftValue);

            if (CheckAllNumbersAchieved(bitMasks, numbers)) {
                return queryIndex + 1;
            }
        }
        return -1;
    }

    private List<HashSet<int>> InitializeBitMasks(int size) {
        var bitMasks = new List<HashSet<int>>(size);
        for (int i = 0; i < size; i++) {
            bitMasks.Add(new HashSet<int> { 0 });
        }
        return bitMasks;
    }

    private bool CheckAllZero(int[] numbers) {
        foreach (var num in numbers) {
            if (num != 0) return false;
        }
        return true;
    }

    private void ApplyBitMask(List<HashSet<int>> bitMasks, int[] numbers, int left, int right, int shiftValue) {
        for (int i = left; i <= right; i++) {
            var updatedSet = new HashSet<int>(bitMasks[i]);
            foreach (var value in bitMasks[i]) {
                updatedSet.Add(value + shiftValue);
            }
            bitMasks[i] = updatedSet;
            bitMasks[i].RemoveWhere(pos => pos > 1000);
        }
    }

    private bool CheckAllNumbersAchieved(List<HashSet<int>> bitMasks, int[] numbers) {
        for (int i = 0; i < numbers.Length; i++) {
            if (!bitMasks[i].Contains(numbers[i])) {
                return false;
            }
        }
        return true;
    }
}

