class Solution {
public:
	// vector<vector<int>> dp;
	unordered_map<int, unordered_map<int, int>> dp;

	int rec(int& ind, int sum, int qind, vector<vector<int>>& queries) {
		if (sum == 0)
			return qind;
		if (qind == queries.size() || sum < 0)
			return INT_MAX;
		if (dp.find(sum) != end(dp) && dp[sum].find(qind) != end(dp[sum]))
			return dp[sum][qind];
		int ans = INT_MAX;
		if (ind >= queries[qind][0] && ind <= queries[qind][1]) {
			ans = rec(ind, sum - queries[qind][2], qind + 1, queries);
		}
		return dp[sum][qind] = min(ans, rec(ind, sum, qind + 1, queries));
	}

	// void cleanDP() {
	// 	for (int i = 0; i < dp.size(); ++i)
	// 	{
	// 		for (int j = 0; j < dp[i].size(); ++j)
	// 		{
	// 			dp[i][j] = -1;
	// 		}
	// 	}
	// }

	int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
		dp.clear();
		// dp.resize(1001, vector<int>(queries.size(), -1));
		int ans = -1;
		for (int i = 0; i < nums.size(); ++i)
		{
			int currAns = rec(i, nums[i], 0, queries);
			if (currAns == INT_MAX)
				return -1;
			ans = max(ans, currAns);
			dp.clear();
		}
		return ans;
	}
};

