    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {

        int maxK = 0; 
        for(int i = 0; i < nums.size(); i++) {
            if(nums[i] == 0)
                continue;
            vector<bool> dp(1001);
            dp[0] = 1;
            for(int j = 0; j < queries.size(); j++) {
                if(queries[j][0] <= i && i <= queries[j][1]) {
                    for(int k = nums[i]; k >= queries[j][2]; k--) {
                        dp[k] = dp[k] || dp[k - queries[j][2]];
                    }
                }

                if(dp[nums[i]]) {
                    maxK = max(maxK, j + 1);
                    break;
                }
            }

            if(!dp[nums[i]]) {
                return -1;
            }
        }
        return maxK;
    }
};

