class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)
        if all(num == 0 for num in nums):
            return 0

        sets = [set([0]) for _ in range(n)]
        def check():
            for i in range(n):
                if nums[i] not in sets[i]:
                    return False
            return True

        for j, (l, r, v) in enumerate(queries):
            for i in range(l, r + 1):
                old_set = sets[i]
                new_set = copy.deepcopy(old_set)
                for old_val in old_set:
                    new_set.add(old_val + v)
                sets[i] = new_set
            if check():
                return j + 1
        return -1

