from typing import List
class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        a = len(nums)
        d = [0] * a  
        for j in range(a):
            d[j] = 1 << 0  
        if all(n == 0 for n in nums):
            return 0
        for k, q in enumerate(queries):
            x, c, v = q
            for j in range(x, c + 1):
                d[j] |= (d[j] << v)  
                for p in range(nums[j] + 1, 1001):
                    d[j] &= ~(1 << p)
            if all((d[j] & (1 << nums[j])) != 0 for j in range(a)):
                return k + 1  
        return -1

