class Solution {
public:
    bool subsetSumToK(int n, int k, vector<int> &arr) {
    vector<bool> prev(k + 1, false);

    prev[0] = true;

    if (arr[0] <= k) {
        prev[arr[0]] = true;
    }

    for (int ind = 1; ind < n; ind++) {
        vector<bool> cur(k + 1, false);

        cur[0] = true;

        for (int target = 1; target <= k; target++) {
            bool notTaken = prev[target];

            bool taken = false;
            if (arr[ind] <= target) {
                taken = prev[target - arr[ind]];
            }

            cur[target] = notTaken || taken;
        }

        prev = cur;
    }

    return prev[k];
}
    int solve(vector<int>& nums, vector<vector<int>>& queries,int mid){
        vector<int> arr(nums.size()+1,0);
          vector<vector<int>> p(nums.size());
        for(int i =0; i<mid; i++){
            arr[queries[i][0]]+=queries[i][2];
            arr[queries[i][1]+1]-=queries[i][2];
            for(int j =queries[i][0]; j<=queries[i][1]; j++){
                p[j].push_back(queries[i][2]);
            }
        }      
        for(int i =1; i<arr.size(); i++){
            arr[i] = arr[i]+arr[i-1];
        }
        for(int i =0; i<nums.size(); i++){
            if(nums[i]<arr[i]){
                if(!subsetSumToK(p[i].size(),nums[i],p[i])) return false;
            }
            else if(nums[i]>arr[i]) return false;
            else continue;
        }
        return true;
    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int st = 0;
        int end = queries.size();
        while(st<=end){
            int mid = (st+end)/2;
            if(solve(nums,queries,mid)) end = mid-1;
            else st = mid+1;
        }
        return (st >queries.size()? -1: st);
    }
};

