            int mid = (low + high) / 2;
            if (fun(n, mid, nums, queries)) {
                ans = mid; // Store the potential answer
                high = mid - 1; // Try for a smaller value
            } else {
                low = mid + 1;
            }
        }
        if(ans==-1) return -1;
        return ans+1;
