                    affectors[num_idx] = list( )
                    subtractors[num_idx] = list( )
                affectors[num_idx].append(idx)
                subtractors[num_idx].append(val)

        result = -1
        for num_idx in range(len(nums)):
            if nums[num_idx] == 0: continue
            if num_idx not in affectors: return -1
            k = -1
            encountered = set([nums[num_idx]])
            for affector, subtractor in zip(affectors[num_idx], subtractors[num_idx]):
                if subtractor in encountered:
                    k = affector; break

                temp = set( )
                for num in encountered:
                    if num - subtractor > 0: temp.add(num - subtractor)
                encountered = encountered.union(temp)

            if k == -1: return -1
            result = max(result, k)
        return result + 1

