class Solution:

    def dict_to_array(self, d):
        result = []
        for key, count in d.items():
            result.extend([key] * count)
        return result

    def can_form_target(self, diff_dict, target):
        dp = [False] * (target + 1)
        dp[0] = True

        nums = self.dict_to_array(diff_dict)

        for num in nums:
            for t in range(target, num - 1, -1):
                if dp[t - num]:
                    dp[t] = True

        return dp[target]

    def check_subsum(self, values, target, current_sum, total_count, count):
        if current_sum == target:
            return True
        elif target == 0:
            return True
        elif count > total_count:
            return False
        elif len(values.keys()) == 0:
            return False
        elif current_sum > target:
            return False


        # print(values)
        # print(current_sum)

        res = False
        keys = list(values.keys())
        for key in keys:
            values[key] -= 1
            if values[key] == 0:
                del values[key]
            res1 = self.check_subsum(values, target, current_sum + key, total_count, count+1)
            if key in values:
                values[key] += 1
            else:
                values[key] = 1
            res2 = self.check_subsum(values, target, current_sum, total_count, count+1)
            res = res or res1 or res2
            if res is True:
                return res

        return res

    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)
        total_sum = 0
        diff_arr = [dict() for _ in range(n+1)]
        k = 0

        for i in range(n):
            #while self.check_subsum(diff_arr[i], nums[i], 0, sum(diff_arr[i].values()), 0 ) == False:
            while self.can_form_target(diff_arr[i], nums[i]) == False:
                k+=1

                if k > len(queries):
                    return -1

                left,right,val = queries[k-1]
                if right >= i:
                    for j in range(max(left, i), right+1):
                        if val in diff_arr[j]:
                            diff_arr[j][val] += 1
                        else:
                            diff_arr[j][val] = 1

        return k

