class Solution {
    fun minZeroArray(nums: IntArray, queries: Array<IntArray>): Int {
        val q = queries.size
        var res = -1

        for ((i, num) in nums.withIndex()) {
            val memo = Array(q) { IntArray(num + 1) }
            val neededQueries = minZeroArray(queries, i, 0, num, memo)
            if (neededQueries == -1) return -1
            res = maxOf(res, neededQueries)
        }

        return res
    }

    fun minZeroArray(queries: Array<IntArray>, i: Int, j: Int, remain: Int, memo: Array<IntArray>): Int {
        val q = queries.size

        if (remain == 0) return j
        if (j == q) return -1

        if (memo[j][remain] != 0) return memo[j][remain]

        val (l, r, v) = queries[j]

        val skip = minZeroArray(queries, i, j + 1, remain, memo)

        if (i !in l..r || v > remain) {
            memo[j][remain] = skip
            return skip
        }

        val notSkip =  minZeroArray(queries, i, j + 1, remain - v, memo)

        var res = 0

        if (skip == -1) {
            res = notSkip
        }
        else if (notSkip == -1) {
            res = skip
        }
        else {
            res = minOf(skip, notSkip)
        }

        memo[j][remain] = res
        return res
    }
}

