class Solution {
public:
    int minZeroArray(vector<int>& a, vector<vector<int>>& q) {
        int n = a.size();
        vector<vector<pair<int, int>>> c(n);
        for(int i = 0; i < q.size(); ++i)
        {
            for(int j = q[i][0]; j <= q[i][1]; ++j)
            {
                c[j].push_back({q[i][2], i});
            }
        }
        int ans = -1;
        for(int i = 0; i < n; ++i)
        {
            if(a[i] == 0)
            {
                ans = max(ans, 0);
                continue;
            }
            int t = -1;
            bitset<1005> bt = 1;
            bt = (bt << a[i]);
            for(int j = 0; j < c[i].size(); ++j)
            {
                bt |= (bt >> c[i][j].first);
                if(bt[0])
                {
                    t = c[i][j].second + 1;
                    break;
                }
            }
            if(t == -1)
            {
                ans = -1;
                break;
            }
            ans = max(ans, t);
        }
        return ans;
    }
};

