class Solution {
    int INF = (int) 1e7;
    Integer[][] dp;
    public int minZeroArray(int[] nums, int[][] queries) {
        int n = nums.length;
        int qs = queries.length;
        int res = -1;
        for(int i = 0; i < n; i++) {
            dp = new Integer[qs][nums[i] + 1];
            int v = dfs(nums[i], i, queries, 0);
            // System.out.println(i + " " + v);
            if(v >= INF) {
                return -1;
            }
            res = Math.max(res, v);
        }

        return res;
    }

    int dfs(int num, int pos, int[][] qs, int idx) {
        if(num == 0) {
            return 0;
        }
        if(num < 0 || idx == qs.length) {
            return INF;
        }
        if(dp[idx][num] != null) {
            return dp[idx][num];
        }
        int curr = dfs(num, pos, qs, idx + 1);
        if(qs[idx][0] <= pos && qs[idx][1] >= pos && num >= qs[idx][2]) {
            curr = Math.min(curr, dfs(num - qs[idx][2], pos, qs, idx + 1));
        }
        return dp[idx][num] = 1 + curr;
    }
}

