class Solution {
private:

    void orIt(vector<bool>& v1, int x, int n) {
        if(x == 0) 
            return; 

        vector<bool> temp = v1;
        for(int i = 0; i <= n; i++) {
            if(v1[i] && i + x <= n) {
                temp[i + x] = true;
            }
        }
        temp[x] = true;

        v1 = temp;
    }

    bool f(int mid , vector<vector<bool>> ref , vector<int>& nums , vector<vector<int>>& q){

        int n = nums.size();
        for(int k = 0; k <= mid; k++) {
            int l = q[k][0] , r = q[k][1] , v = q[k][2];

            for(int j = l; j <= r; j++){
                if(nums[j] > 0)
                    orIt(ref[j], v, nums[j]);
            }

        }

        bool b = true;
        for(int j = 0; j < n; j++){
            if(!ref[j][nums[j]]){
                b = false;
                break;
            }
        }

        return b;

    }

public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& q) {

        int n = nums.size() , nq = q.size();       
        if(count(nums.begin(), nums.end(), 0) == n)
            return 0;


        vector<vector<bool>> ref(n , vector<bool>(1001 , false));
        for(int i = 0; i < n; i++) {
            ref[i][0] = true; 
        }

        int low = 0 , high = nq-1 , ans = -1;

        while(low <= high){

            int mid = low + (high - low)/2;
            if(f(mid , ref , nums  , q)){
                ans = mid+1;
                high = mid-1;
            }
            else
                low = mid+1;

        }

        return ans;

    }
};

